    private void spnFatStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_spnFatStateChanged
        // TODO add your handling code here:
        
        int num = Integer.parseInt(String.valueOf(spnFat.getValue()));
        int result=1, i=1;
        String exibe=" ";
        while (i<=num){
           exibe = exibe + " x " + i;
           result = result * i;
           i++;
        }
        lblNum.setText(exibe);
        lblResult.setText(Integer.toString(result));
    }//GEN-LAST:event_spnFatStateChanged

